        <script>
            $(document).ready(function() {
                var mybutton = $("#btn-back-to-top");

                $(window).scroll(function() {
                    if ($(this).scrollTop() > 20) {
                        mybutton.removeClass('d-none').fadeIn();
                    } else {
                        mybutton.fadeOut();
                    }
                });

                mybutton.click(function() {
                    $("html, body").animate({ scrollTop: 0 }, "slow");
                    return false;
                });
            });
        </script>

